<?	/** @var $form CActiveForm */	/** @var $formData ChangePasswordForm */	$cs = Yii::app()->clientScript;	$cs->registerCssFile(Yii::app()->getBaseUrl(true) . '/vendor/mobile/css/jquery.mobile.ios.theme.css?' . Yii::app()->params['version']);	$cs->registerCssFile(Yii::app()->getBaseUrl(true) . '/css/phone/login.css?' . Yii::app()->params['version']);	$cs->registerScriptFile(Yii::app()->getBaseUrl(true) . '/vendor/mobile/js/jquery.mobile.js?' . Yii::app()->params['version'], CClientScript::POS_END);	$this->renderPartial('../site/theme');	$this->pageTitle = Yii::app()->name . ' - Login';?><div data-role="page" id="main" data-cache="never" data-dom-cache="false" data-ajax="false" data-theme="a">	<div data-role="content" class="login-content">		<?			$form = $this->beginWidget('CActiveForm', array(				'action' => Yii::app()->createUrl('auth/changePasswordSeparate'),				'htmlOptions' => array('data-ajax' => 'false')			));			echo $form->textField($formData, 'login'				, array(					'input type' => 'hidden')			);			echo $form->textField($formData, 'oldPassword'				, array(					'input type' => 'hidden')			);			echo $form->textField($formData, 'rememberMe'				, array(					'input type' => 'hidden')			);		?>		<p>You are logged as: <? echo $formData->login ?><br> You need to change your temporary password</p>		<div class="edit-fields">			<?				echo $form->textField($formData, 'newInitialPassword'					, array(						'class' => 'edit-field',						'input type' => 'Password',						'placeholder' => 'New Password')				);				echo $form->textField($formData, 'newRepeatPassword'					, array(						'class' => 'edit-field',						'input type' => 'Password',						'placeholder' => 'Type Password Again')				);				echo $form->error($formData, 'newInitialPassword'					, array('class' => 'error-message')				);				echo $form->error($formData, 'newRepeatPassword'					, array('class' => 'error-message')				);			?>		</div>		<div class="login-button-container">			<?				echo CHtml::submitButton('Save'					, array('class' => 'btn',						'data-theme' => 'd',						'data-role' => 'button',					));			?>		</div>		<? $this->endWidget(); ?>	</div</div>