<?	/** @var $form CActiveForm */	/** @var $formData LoginForm */	$cs = Yii::app()->clientScript;	$cs->registerCoreScript('jquery');	$cs->registerCssFile(Yii::app()->getBaseUrl(true) . '/vendor/mobile/jquery.mobile-1.2.0.css?' . Yii::app()->params['version']);	$cs->registerScriptFile(Yii::app()->getBaseUrl(true) . '/vendor/mobile/jquery.mobile-1.2.0.js?' . Yii::app()->params['version'], CClientScript::POS_HEAD);	$cs->registerCssFile(Yii::app()->getBaseUrl(true) . '/css/phone/login.css?' . Yii::app()->params['version']);	$cs->registerScriptFile(Yii::app()->getBaseUrl(true) . '/js/phone/login.js?' . Yii::app()->params['version'], CClientScript::POS_HEAD);	$this->pageTitle = Yii::app()->name . ' - Login';?><div data-role="page" id="main" data-cache="never" data-dom-cache="false" data-ajax="false">	<div data-role='header' class="page-header" data-position="fixed" data-theme="b"></div>	<div data-role="content">		<?			$form = $this->beginWidget('CActiveForm', array(				'action' => Yii::app()->createUrl('auth/login'),				'htmlOptions' => array('data-ajax' => 'false')			));		?>		<table class="form-login">			<tr>				<td>					<img id="image-logo" src="<? echo Yii::app()->baseUrl . '/images/logo_phone.png'; ?>"/>				</td>			</tr>			<tr>				<td>					<?						echo $form->textField($formData, 'login'							, array(								'id' => 'field-login',								'class' => 'edit-field',								'placeholder' => 'Username')						);					?>				</td>			</tr>			<tr>				<td>					<?						echo $form->textField($formData, 'password'							, array(								'id' => 'field-password',								'class' => 'edit-field',								'input type' => 'Password',								'placeholder' => 'Password')						);					?>				</td>			</tr>			<? if (Yii::app()->params['login']['rememberMeField']): ?>				<tr>					<td>						<?							echo $form->checkBox($formData, 'rememberMe'								, array(									'id' => 'field-remember',									'data-theme' => 'c')							);							echo $form->labelEx($formData, 'rememberMe'								, array(									'id' => 'lable-remember',									'for' => 'field-remember'								)							);						?>					</td>				</tr>			<? endif; ?>			<tr>				<td>					<?						echo $form->error($formData, 'login'							, array('class' => 'error-message')						);						echo $form->error($formData, 'password'							, array('class' => 'error-message')						);					?>				</td>			</tr>			<tr>				<td style="padding: 2%;">					<?						echo CHtml::submitButton('Log In'							, array('id' => 'button-login',								'class' => 'btn',								'data-theme' => 'b',								'data-role' => 'button',							));					?>				</td>			</tr>		</table>		<? $this->endWidget(); ?>		<br>		<? if (Yii::app()->params['login']['forgotPasswordField'] || (Yii::app()->browser->isMobile() && !($this->browser == Browser::BROWSER_IPHONE || $this->browser == Browser::BROWSER_ANDROID_MOBILE))): ?>			<table width="100%">				<tr>					<? if (Yii::app()->params['login']['forgotPasswordField']): ?>						<td width="50%" style="padding: 2%;">							<a id="forgot-password" href="#recover-password" data-role="button" data-theme="b" data-mini="true"							   data-transition="slidefade">Forgot Password</a>						</td>					<? endif; ?>					<? if (Yii::app()->browser->isMobile() && !($this->browser == Browser::BROWSER_IPHONE || $this->browser == Browser::BROWSER_ANDROID_MOBILE)): ?>						<td width="50%" style="padding: 2%;">							<a id="button-switch-version" data-role="button" data-theme="b" data-mini="true">Full version</a>						</td>					<? endif; ?>				</tr>			</table>		<? endif; ?>	</div>	<div data-role='footer' class="page-header" data-position="fixed" data-theme="b"></div></div><? if (Yii::app()->params['login']['disclaimer']): ?>	<div data-role="page" id="disclaimer" data-cache="never" data-dom-cache="false" data-ajax="false">		<div data-role='header' class="page-header" data-position="fixed" data-theme="b"></div>		<div 