<table class="tool-dialog">	<tr>		<td colspan="2">			<legend>Email Page - <?echo $page->title;?></legend>			<span class="help-block"><?echo $page->getUrl();?></span>		</td>	</tr>	<tr>		<td class="aligned-top">			<div id="email-to-select" class="dropdown">				<button class="btn btn-block control-label dropdown-toggle <?php if (!isset($availableEmails)): ?>disabled<?php endif; ?>" type="button" data-toggle="dropdown">To:</button>				<div class="dropdown-menu" role="menu">					<ul>						<?php if (isset($availableEmails)): ?>							<?php foreach ($availableEmails as $email): ?>								<li>									<label class="checkbox">										<input class="existed-email" type="checkbox" value="<?php echo $email; ?>">										<?php echo $email; ?>									</label>								</li>							<?php endforeach; ?>						<?php endif; ?>					</ul>					<button class="btn btn-small btn-block apply-selection" type="button">Apply</button>				</div>			</div>		</td>		<td class="aligned-top">			<textarea id="email-to" rows="1" class="input-block-level"></textarea>		</td>	</tr>	<tr>		<td class="aligned-top">			<div id="email-to-copy-select" class="dropdown">				<button class="btn btn-block control-label dropdown-toggle <?php if (!isset($availableEmails)): ?>disabled<?php endif; ?>" type="button" data-toggle="dropdown">Cc:</button>				<div class="dropdown-menu" role="menu">					<ul>						<?php if (isset($availableEmails)): ?>							<?php foreach ($availableEmails as $email): ?>								<li>									<label class="checkbox">										<input class="existed-email" type="checkbox" value="<?php echo $email; ?>">										<?php echo $email; ?>									</label>								</li>							<?php endforeach; ?>						<?php endif; ?>					</ul>					<button class="btn btn-small btn-block apply-selection" type="button">Apply</button>				</div>			</div>		</td>		<td class="aligned-top">			<textarea id="email-to-copy" class="input-block-level" rows="1"></textarea>		</td>	</tr>	<tr>		<td class="title">			<label class="control-label">From:</label>		</td>		<td>			<input type="text" id="email-from" class="input-block-level" value="<?php echo Yii::app()->user->email; ?>">		</td>	</tr>	<tr>		<td class="title">			<label class="control-label">Subject:</label>		</td>		<td>			<input type="text" id="email-subject" class="input-block-level" value="">		</td>	</tr>	<tr>		<td class="aligned-top">			<label class="control-label">Body:</label>		</td>		<td>			<textarea id="email-body" class="input-block-level" rows="3"></textarea>		</td>	</tr>	<tr>		<td colspan="2">			<label class="checkbox"><input id="email-to-me" type="checkbox" value="">Send a Copy to me</label>		</td>	</tr>	<tr>		<td colspan="2" class="buttons-area">			<button class="btn" id="accept-button" type="button">Email</button>			<button class="btn" id="cancel-button" type="button">Cancel</button>		</td>	</tr></table>