<table class="tool-dialog">	<tr>		<td colspan="2">			<legend>Email link</legend>		</td>	</tr>	<tr>		<td colspan="2">			<form class="form-horizontal">				<div class="form-group">					<div class="col-xs-2">						<div id="email-to-select" class="dropdown">							<button class="btn btn-default dropdown-toggle <? if (!isset($availableEmails)): ?>disabled<? endif; ?>" type="button" data-toggle="dropdown">								<strong>To:</strong></button>							<div class="dropdown-menu" role="menu">								<ul>									<? if (isset($availableEmails)): ?>										<? foreach ($availableEmails as $email): ?>											<li>												<div class="checkbox">													<label>														<input class="existed-email" type="checkbox" value="<? echo $email; ?>">														<? echo $email; ?>													</label>												</div>											</li>										<? endforeach; ?>									<? endif; ?>								</ul>								<button class="btn btn-default btn-sm btn-block apply-selection" type="button">Apply</button>							</div>						</div>					</div>					<div class="col-xs-10">						<label for="email-to" class="sr-only"></label><textarea id="email-to" class="form-control" rows="1"></textarea>					</div>				</div>				<div class="form-group">					<div class="col-xs-2">						<div id="email-to-copy-select" class="dropdown">							<button class="btn btn-default dropdown-toggle <? if (!isset($availableEmails)): ?>disabled<? endif; ?>" type="button" data-toggle="dropdown">								<strong>Cc:</strong></button>							<div class="dropdown-menu" role="menu">								<ul>									<? if (isset($availableEmails)): ?>										<? foreach ($availableEmails as $email): ?>											<li>												<div class="checkbox">													<label>														<input class="existed-email" type="checkbox" value="<? echo $email; ?>">														<? echo $email; ?>													</label>												</div>											</li>										<? endforeach; ?>									<? endif; ?>								</ul>								<button class="btn btn-default btn-sm btn-block apply-selection" type="button">Apply</button>							</div>						</div>					</div>					<div class="col-xs-10">						<label for="email-to-copy" class="sr-only"></label><textarea id="email-to-copy" class="form-control" rows="1"></textarea>					</div>				</div>				<div class="form-group">					<label for="email-from" class="col-xs-2 control-label">From:</label>					<div class="col-xs-10">						<input type="text" id="email-from" class="form-control" value="<? echo Yii::app()->user->email; ?>">					</div>				</div>				<div class="form-group">					<label for="email-subject" class="col-xs-2 control-label">Subject:</label>					<div class="col-xs-10">						<input type="text" id="email-subject" class="form-control" value="<? echo Yii::app()->params['email']['send_link']['subject']; ?>">					</div>				</div>				<div class="form-group">					<label for="email-body" class="col-xs-2 control-label">Body:</label>					<div class="col-xs-10">						<textarea id="email-body" class="form-control" rows="3"><? echo Yii::app()->params['email']['send_link']['body']; ?></textarea>					</div>				</div>				<div class="form-group">					<label for="expires-in" class="col-xs-2 control-label">Expires After:</label>					<div class="col-xs-10">						<select id="expires-in" class="form-control">							<option selected value="7">7 days</option>							<option value="30">30 days</option>							<option value="90">90 days</option>							<option value="">Never</option>						</select>					</div>				</div>				<div class="form-group">					<div class="col-xs-10 col-xs-offset-2">						<div class="checkbox">							<label>								<input id="email-to-me" type="checkbox" value="">Send a Copy to me							</label>						</div>					</div>				</div>			</form>		</td>	</tr>	<tr>		<td colspan="2" class="buttons-area">			<button class="btn btn-default" id="accept-button" type="button">Email</button>			<button class="btn btn-default" id="cancel-button" type="button">Cancel</button>		</td>	</tr></table>